import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useParams, useNavigate } from 'react-router-dom';

const GameContainer = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
  text-align: center;
`;

const BackButton = styled.button`
  background-color: #ff6b8b;
  color: white;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 25px;
  font-size: 1rem;
  cursor: pointer;
  margin-bottom: 1rem;
  transition: background-color 0.2s;

  &:hover {
    background-color: #ff4d6d;
  }
`;

const Message = styled.div`
  background-color: white;
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin: 2rem 0;
`;

const games = [
  {
    id: 1,
    title: '6-4반의 첫 번째 게임',
    description: '6-4반 친구가 만든 게임입니다.',
    image: 'https://placekitten.com/300/200',
    scratchUrl: 'https://scratch.mit.edu/projects/1173182570',
    classNumber: '6-4'
  },
  {
    id: 2,
    title: '6-4반의 두 번째 게임',
    description: '6-4반 친구가 만든 게임입니다.',
    image: 'https://placekitten.com/301/200',
    scratchUrl: 'https://scratch.mit.edu/projects/987654321',
    classNumber: '6-4'
  },
  {
    id: 3,
    title: '6-5반의 첫 번째 게임',
    description: '6-5반 친구가 만든 게임입니다.',
    image: 'https://placekitten.com/302/200',
    scratchUrl: 'https://scratch.mit.edu/projects/1173182570',
    classNumber: '6-5'
  },
  {
    id: 4,
    title: '6-5반의 두 번째 게임',
    description: '6-5반 친구가 만든 게임입니다.',
    image: 'https://placekitten.com/303/200',
    scratchUrl: 'https://scratch.mit.edu/projects/987654321',
    classNumber: '6-5'
  }
];

const GamePlay: React.FC = () => {
  const { gameId } = useParams();
  const navigate = useNavigate();

  const game = games.find(g => g.id === Number(gameId));

  useEffect(() => {
    if (game) {
      window.open(game.scratchUrl, '_blank');
    }
  }, [game]);

  if (!game) {
    return <div>게임을 찾을 수 없습니다.</div>;
  }

  return (
    <GameContainer>
      <BackButton onClick={() => navigate('/')}>
        ← 게임 목록으로 돌아가기
      </BackButton>
      <Message>
        <h2>{game.title}</h2>
        <p>게임이 새 창에서 열립니다. 창이 열리지 않았다면 아래 링크를 클릭해주세요.</p>
        <a href={game.scratchUrl} target="_blank" rel="noopener noreferrer">
          게임 열기
        </a>
      </Message>
    </GameContainer>
  );
};

export default GamePlay;
