import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import logo from '../logo.svg';

const Nav = styled.nav`
  background-color: white;
  padding: 1rem 2rem;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  font-family: 'Cafe24Ssurround', sans-serif;
`;

const NavContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
  cursor: pointer;
`;

const LogoImage = styled.img`
  height: 40px;
  width: auto;
`;

const Logo = styled.div`
  color: #ff6b8b;
  font-size: 1.8rem;
  font-weight: normal;
`;

const MenuContainer = styled.div`
  display: flex;
  gap: 2rem;
  position: relative;
`;

const MenuButton = styled.button`
  background: none;
  border: none;
  color: white;
  font-size: 1.3rem;
  cursor: pointer;
  padding: 0.5rem 1rem;
  position: relative;
  font-weight: normal;

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 5px;
  }
`;

const DropdownMenu = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  background-color: white;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  padding: 0.5rem 0;
  min-width: 200px;
  display: none;

  ${MenuButton}:hover & {
    display: block;
  }
`;

const DropdownItem = styled.a`
  display: block;
  padding: 0.8rem 1.5rem;
  color: #333;
  text-decoration: none;
  transition: background-color 0.2s;
  font-size: 1.1rem;
  font-weight: normal;
  font-family: 'Cafe24Ssurround', sans-serif;

  &:hover {
    background-color: #fff5f7;
    color: #ff6b8b;
  }
`;

const Navbar: React.FC = () => {
  const navigate = useNavigate();

  const handleNavigate = (path: string) => {
    navigate(path);
  };

  return (
    <Nav>
      <NavContainer>
        <LogoContainer onClick={() => handleNavigate('/')}>
          <LogoImage src={logo} alt="Logo" />
          <Logo>게임 컬렉션</Logo>
        </LogoContainer>
        <MenuContainer>
          <MenuButton onClick={() => handleNavigate('/class/6-4')}>
            6-4반
            <DropdownMenu>
              <DropdownItem onClick={() => handleNavigate('/class/6-4/racing')}>레이싱 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-4/breakout')}>브레이크아웃 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-4/reaction')}>반응 속도 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-4/free')}>자유 게임</DropdownItem>
            </DropdownMenu>
          </MenuButton>
          <MenuButton onClick={() => handleNavigate('/class/6-5')}>
            6-5반
            <DropdownMenu>
              <DropdownItem onClick={() => handleNavigate('/class/6-5/racing')}>레이싱 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-5/breakout')}>브레이크아웃 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-5/reaction')}>반응 속도 게임</DropdownItem>
              <DropdownItem onClick={() => handleNavigate('/class/6-5/free')}>자유 게임</DropdownItem>
            </DropdownMenu>
          </MenuButton>
          <MenuButton onClick={() => handleNavigate('/awards')}>수상작</MenuButton>
        </MenuContainer>
      </NavContainer>
    </Nav>
  );
};

export default Navbar;
