import React from 'react';
import styled from 'styled-components';
import { useNavigate, useParams } from 'react-router-dom';

const GameGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const GameCard = styled.div`
  background-color: white;
  border-radius: 15px;
  padding: 1.5rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.2s;
  cursor: pointer;

  &:hover {
    transform: translateY(-5px);
  }
`;

const GameImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 10px;
  margin-bottom: 1rem;
`;

const GameTitle = styled.h2`
  color: #ff6b8b;
  margin: 0 0 0.5rem 0;
  font-size: 1.5rem;
`;

const GameDescription = styled.p`
  color: #666;
  margin: 0;
  font-size: 1rem;
`;

const BackButton = styled.button`
  background-color: #ff6b8b;
  color: white;
  border: none;
  padding: 0.8rem 1.5rem;
  border-radius: 25px;
  font-size: 1rem;
  cursor: pointer;
  margin: 1rem 2rem;
  transition: background-color 0.2s;

  &:hover {
    background-color: #ff4d6d;
  }
`;

const gamesByClass = {
  '6-4': [
    {
      id: 1,
      title: '6-4반의 첫 번째 게임',
      description: '6-4반 친구가 만든 게임입니다.',
      image: 'https://placekitten.com/300/200',
      scratchUrl: 'https://scratch.mit.edu/projects/1173182570'
    },
    {
      id: 2,
      title: '6-4반의 두 번째 게임',
      description: '6-4반 친구가 만든 게임입니다.',
      image: 'https://placekitten.com/301/200',
      scratchUrl: 'https://scratch.mit.edu/projects/987654321'
    }
  ],
  '6-5': [
    {
      id: 1,
      title: '6-5반의 첫 번째 게임',
      description: '6-5반 친구가 만든 게임입니다.',
      image: 'https://placekitten.com/302/200',
      scratchUrl: 'https://scratch.mit.edu/projects/1173182570'
    },
    {
      id: 2,
      title: '6-5반의 두 번째 게임',
      description: '6-5반 친구가 만든 게임입니다.',
      image: 'https://placekitten.com/303/200',
      scratchUrl: 'https://scratch.mit.edu/projects/987654321'
    }
  ]
};

const GameList: React.FC = () => {
  const navigate = useNavigate();
  const { classNumber } = useParams<{ classNumber: string }>();

  const games = classNumber ? gamesByClass[classNumber as keyof typeof gamesByClass] : [];

  const handleGameClick = (gameId: number) => {
    navigate(`/class/${classNumber}/play/${gameId}`);
  };

  return (
    <>
      <BackButton onClick={() => navigate('/')}>
        ← 반 선택으로 돌아가기
      </BackButton>
      <GameGrid>
        {games.map((game) => (
          <GameCard key={game.id} onClick={() => handleGameClick(game.id)}>
            <GameImage src={game.image} alt={game.title} />
            <GameTitle>{game.title}</GameTitle>
            <GameDescription>{game.description}</GameDescription>
          </GameCard>
        ))}
      </GameGrid>
    </>
  );
};

export default GameList;
